using BenchmarkDotNet.Attributes;
using BenchmarkDotNet.Running;
using ExtractNumbers.TestBed;
using System.Data.SqlTypes;

namespace ExtractNumbers.Benchmarks
{
    class Program
    {
        static void Main(string[] args)
        {
            var summary = BenchmarkRunner.Run(typeof(Program).Assembly);
        }
    }

    [MemoryDiagnoser]
    public class Comparisons
    {
        [Params
        (
            "AAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAA",
            "11111111111111111111AAAAAAAAAAAAAAAAAAAA",
            "1111111111111111111111111111111111111111"//,
            //"",
            //"",
            //""
        )]
        public string Input { get; set; }

        [Benchmark]
        public SqlString ExtractNumbers() 
            => UserDefinedFunctions.ExtractNumbersClr(Input);

        [Benchmark]
        public SqlString ExtractNumbersSBLengthOneHalf()
            => TestFunctions.ExtractNumbersSBLengthOneHalf(Input);

        [Benchmark]
        public SqlString ExtractNumbersSBLength4000()
            => TestFunctions.ExtractNumbersSBLength4000(Input);

        [Benchmark]
        public SqlString ExtractNumbersRegEx()
            => TestFunctions.ExtractNumbersRegEx(Input);

        [Benchmark]
        public SqlString ExtractNumbersRegEx_Join()
            => TestFunctions.ExtractNumbersRegEx_Join(Input);

        [Benchmark]
        public SqlString ExtractNumbersRegEx_SB()
            => TestFunctions.ExtractNumbersRegEx_SB(Input);
    }
}
